const disclaimer=document.getElementById("login-disclaimer");document.getElementById("login-button").addEventListener("click",()=>{const a=document.getElementById("username").value;if(24<a.length)disclaimer.innerHTML="Username or password is too long. Max 24 characters.";else{fetch("/create",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:a})}).then(a=>a.json()).then(a=>{console.log("SERVER RESPONSE:",a),disclaimer.innerHTML="Success. Redirecting...",window.location.href="/maze-list"}).catch(a=>{console.error("SERVER RESPONSE (ERROR):",a),disclaimer.innerHTML="Error: Internal Server Error."})}});